INSERT INTO simple_bbs.t_main (m_mainid, m_title, m_content, m_mainerid, m_sectionid, m_isontop, m_isperfect, m_maindate, m_point) VALUES (128, '丽江反杀案：正当防卫还是故意杀人？', '<p>丽江反杀案：舆论不能捆绑司法<br />
